import React, {useState} from "react"

export default () => {
    const [modal, setModal] = useState(false)
    const [modalContent, setModalContent] = useState("It's a model content")

    const handleModal = (content = false, fromOtherModal = false) => {
        if (!fromOtherModal)
            setModal(!modal)

        if (content) {
            setModalContent(content)
        }
    }

    return {modal, handleModal, modalContent}
}
