import React, {useEffect, useState} from "react"

export default () => {
    const [modal, setModal] = useState(false)
    const [modalContent, setModalContent] = useState("It's a model content")

    const handleModal = (content = false, fromOtherModal = false) => {

        if (content === false && fromOtherModal === false) {
            let el = document.getElementById('body')
            el.setAttribute('style', "")

            showScrollHedUp()
        } else {
            let el = document.getElementById('body')
            el.setAttribute('style', "overflow-x: hidden !important; overflow-y: hidden !important;")

            hideScrollHedUp()
        }

        if (!fromOtherModal)
            setModal(!modal)


        if (content) {
            setModalContent(content)
        }
    }

    const hideScrollHedUp = () => {
        let hedDownEl = document.getElementById('hed_down')
        hedDownEl.classList.remove('scroll')
    }

    const showScrollHedUp = () => {
        if (document.scrollTop > 65) {
            let hedDownEl = document.getElementById('hed_down')
            hedDownEl.classList.add('scroll')
        }
    }

    return {modal, handleModal, modalContent}
}
