import React, {useContext, useEffect, useState} from 'react'
import {modalContext} from 'contexts/modalContext'
import useFetch from 'hooks/useFetch'

const SelectServices = params => {
    const [checkedItems, setCheckedItems] = useState([])
    const {modalContent, handleModal, modal} = useContext(modalContext)
    const [{isLoading, response, error}, doFetch] = useFetch('/services/findByCategory?categoryTitle=saleAutoParts')

    useEffect(() => {
        doFetch()
    }, [doFetch])

    useEffect(() => {
        setCheckedItems(params.selectedServices)
    }, [])

    useEffect(() => {
        params.setSelectedServices(checkedItems)
    }, [checkedItems])

    const handleServiceClick = (event, item) => {
        let updatedItems = [...checkedItems];

        if (event.target.checked) {
            updatedItems.push(item)
        } else {
            const ids = updatedItems.map(el => el.id)
            const index = ids.indexOf(item.id)
            updatedItems.splice(index, 1)
        }

        setCheckedItems(updatedItems)
    }

    const elChecked = id => {
        const ids = checkedItems.map(el => el.id)
        if (ids.includes(id))
            return true
    }

    const handleSaveClick = event => {
        event.preventDefault()
        handleModal()
    }


    return (
        <div className="modal_big modal_usl" style={{display: 'block'}}>
            <img onClick={() => handleModal()} className="modal_close" src="/img/close.svg" alt={'close'}/>
            <div className="modal_big_hed">
                <div className="container">
                    <h2>Выберите услуги, которые оказывает Ваш автосервис</h2>
                </div>
            </div>
            <div className="modal_big_cont" style={{overflowX: 'hidden'}}>
                <div className="container">
                    <div className="modal_big_usl_row">
                        <div className="modal_big_cont_usl_item">
                            {response && (
                                <ul className="selectServices">
                                    {
                                        response.map((service, index) => (
                                            <li key={index} onChange={event => handleServiceClick(event, service)}>
                                                <label>
                                                    <input type="checkbox" defaultChecked={elChecked(service.id)}/>
                                                    <div>{service.title}</div>
                                                </label>
                                            </li>
                                        ))
                                    }
                                </ul>
                            )}
                        </div>
                    </div>
                </div>
            </div>
            <div className="modal_big_foot">
                <div className="container">
                    <a onClick={handleSaveClick} className="but1">Сохранить</a>
                </div>
            </div>
        </div>
    )
}

export default SelectServices
